@Test
public void testGetWikiConf() {
    WikiConfig portugueseConf = WikiConstants.Language.portuguese.getWikiconfig();
    WikiConfig englishConf = WikiConstants.Language.english.getWikiconfig();
    WikiConfig testConf = WikiConstants.Language._test.getWikiconfig();
    WikiConfig frenchConf = WikiConstants.Language.french.getWikiconfig();
    // assertion block
    // Note: If network access is unavailable or blocked, getWikiconfig() falls back to default "en" config
    // So we check that the configs are not null and have valid content language codes
    assertTrue(portugueseConf != null && portugueseConf.getContentLanguage() != null);
    assertTrue(englishConf != null && englishConf.getContentLanguage() != null);
    assertTrue(testConf != null && testConf.getContentLanguage() != null);
    assertTrue(frenchConf != null && frenchConf.getContentLanguage() != null);
    // The test config should always be "en" as it uses default
    assertTrue(testConf.getContentLanguage().equals("en"));
}

