*/
public class FlashE2ETest extends AbstractE2ETest {

    @Override
    protected Shutdownable configureAndStartHttpServer(HttpServer server) {
        return
            server
                .setFilter(enableFlash())
                .register(GET, "/a", (r) -> {
                    flash(r).set("name", "titanite");
                    flash(r).set("lorem", "ipsum");
                    return ok().toFuture();
