        byte[] in = TestUtils.generateRandomBlob(65536);
        byte[] key = DigestUtils.md5(in);
        session.put(org.stem.client.Blob.create(key, in));
        org.stem.client.Blob blob = session.get(key);
        Assert.assertArrayEquals(blob.body, in);
    }

