    @Test public void
    dollar_methods() {
        Key<String> name = $("name");
        Key<Integer> age = $("age");
        MapKey<String> phoneNumbers = $M("phoneNumbers");
        RecordKey address = $R("address");
        ListKey<String> addressLines = $L("addressLines");
