package com.ibm.research.ai.ki.util;

import java.util.*;

public abstract class NextOnlyIterator<T> implements Iterator<T>, AutoCloseable {
	private T next;
	private boolean done = false;

	abstract protected T getNext();


	@Override
	public void remove() {
		throw new UnsupportedOperationException();
	}

	@Override
	public boolean hasNext() {
		if (done)
			return false;
		if (next == null)
			next = getNext();
		done = next == null;
		if (done)
			close();
		return !done;
	}

	@Override
	public T next() {
		if (done)
			return null;
		if (next != null) {
			T toRet = next;
			next = null;
			return toRet;
		}
		T toRet = getNext();
		done = toRet == null;
		if (done)
			close();
		return toRet;
	}

	public void close() {}

	@Override
	protected void finalize() throws Throwable {
	    super.finalize();
	    if (!done)
	    	close();
	}
}

