public JcsegServer registerHandler()
{
    String basePath = this.getClass().getPackage().getName()+".controller";
    AbstractRouter router = new DynamicRestRouter(basePath, MainController.class);
    router.addMapping("/extractor/keywords", KeywordsController.class);
    router.addMapping("/extractor/keyphrase", KeyphraseController.class);
    router.addMapping("/extractor/sentence", SentenceController.class);
    router.addMapping("/extractor/summary", SummaryController.class);
    router.addMapping("/tokenizer/default", TokenizerController.class);

    /*
     * the rest of path and dynamic rest checking will handler it 
    */
    //router.addMapping("/tokenizer/default", TokenizerController.class);

    /*
     * Create a ServletContextHandler to wrap the handlers for Jetty 12
    */
    ServletContextHandler context = new ServletContextHandler(ServletContextHandler.SESSIONS);
    context.setContextPath("/");

    /*
     * Create a unified servlet that dispatches to our handlers
    */
    HttpServlet unifiedServlet = new HttpServlet() {
        private StandardHandler stdHandler = new StandardHandler(config, resourcePool, router);
        private JcsegResourceHandler resourceHandler = new JcsegResourceHandler();

        @Override
        protected void service(HttpServletRequest req, HttpServletResponse resp) 
                throws ServletException, java.io.IOException {
            String requestUri = req.getRequestURI();
            // Resource requests go to resource handler
            if (requestUri.length() > 1 && requestUri.indexOf('.') > -1) {
                resourceHandler.handle(requestUri, req, resp);
            } else {
                // API requests go to standard handler
                stdHandler.handle(requestUri, req, resp);
            }
        }
    };

    context.addServlet(new ServletHolder(unifiedServlet), "/*");

    /*
     * Apply Gzip compression
    */
    GzipHandler gzipHandler = new GzipHandler();
    gzipHandler.setHandler(context);
    server.setHandler(gzipHandler);

    return this;
}

