public WikiConfig getWikiconfig(Language this) {
    WikiConfig config = DefaultConfigEnWp.generate();
    if (this != Language._test) {
        // We need to get the ISO language code from the language name
        String langName = this.name().replace('_', ' ');
        String langCode = null;

        try {
            // Try to find the language code using the nv-i18n library
            List<LanguageCode> langCodes = LanguageCode.findByName(".*" + langName + ".*");
            if (!langCodes.isEmpty()) {
                langCode = langCodes.get(0).name().toLowerCase();
            } else {
                // If not found, try direct mapping for common cases
                switch (this) {
                    case portuguese: langCode = "pt"; break;
                    case english: langCode = "en"; break;
                    case french: langCode = "fr"; break;
                    case german: langCode = "de"; break;
                    case spanish: langCode = "es"; break;
                    case italian: langCode = "it"; break;
                    case dutch: langCode = "nl"; break;
                    case polish: langCode = "pl"; break;
                    case russian: langCode = "ru"; break;
                    case japanese: langCode = "ja"; break;
                    case chinese: langCode = "zh"; break;
                    default:
                        // Try looking up by the capitalized name
                        langName = langName.substring(0, 1).toUpperCase() + langName.substring(1);
                        langCodes = LanguageCode.findByName(langName);
                        if (!langCodes.isEmpty()) {
                            langCode = langCodes.get(0).name().toLowerCase();
                        }
                }
            }

            if (langCode != null) {
                return LanguageConfigGenerator.generateWikiConfig(langCode);
            }
        } catch (IOException | ParserConfigurationException | SAXException e) {
            System.out.println(
                    String.format("Failed to create WikiConfig for language for %s, using default instead",
                            langName)
            );
        }
    }
    return config;
}

