    private Integer order;
    //支持的消费内容
    private Set<String> consumes;
    //支持的生产内容
    private Set<String> produces;
    //异常处理器
    private List<String> errors = new ArrayList<>(3);

    public RouteConfig() {

    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

