public class RestClient {

  private RestTemplate restTemplate;
  private HttpHeaders httpHeaders;
  private HttpStatusCode httpStatusCode;

  public RestClient() {
    this.restTemplate = new RestTemplate();
    this.httpHeaders = new HttpHeaders();
  }

  public void addHeader(String headerKey, String headerValue) {
    this.httpHeaders.add(headerKey, headerValue);
  }

  public String get(String uri) {
    HttpEntity<String> requestEntity = new HttpEntity<>("", httpHeaders);
    ResponseEntity<String> responseEntity = restTemplate
        .exchange(uri, HttpMethod.GET, requestEntity, String.class);
    this.setHttpStatusCode(responseEntity.getStatusCode());
    return responseEntity.getBody();
  }

  public String post(String uri, String json) {
    HttpEntity<String> requestEntity = new HttpEntity<>(json, httpHeaders);
    ResponseEntity<String> responseEntity = restTemplate
        .exchange(uri, HttpMethod.PUT, requestEntity, String.class);
    this.setHttpStatusCode(responseEntity.getStatusCode());
    return responseEntity.getBody();
  }

  public void put(String uri, String json) {
    HttpEntity<String> requestEntity = new HttpEntity<String>(json, httpHeaders);
    ResponseEntity<String> responseEntity = restTemplate
        .exchange(uri, HttpMethod.PUT, requestEntity, (Class<String>) null);
    this.setHttpStatusCode(responseEntity.getStatusCode());
  }

  public void delete(String uri) {
    HttpEntity<String> requestEntity = new HttpEntity<String>("", httpHeaders);
    ResponseEntity<String> responseEntity = restTemplate
        .exchange(uri, HttpMethod.DELETE, requestEntity, (Class<String>) null);
    this.setHttpStatusCode(responseEntity.getStatusCode());
  }

  public HttpStatusCode getHttpStatusCode() {
    return httpStatusCode;
  }

  public void setHttpStatusCode(HttpStatusCode httpStatusCode) {
    this.httpStatusCode = httpStatusCode;
  }

  // Backward compatibility method
  public HttpStatus getHttpStatus() {
    return HttpStatus.valueOf(httpStatusCode.value());
  }

  public void setHttpStatus(HttpStatus httpStatus) {
    this.httpStatusCode = httpStatus;
  }
}

