    public void testBinary() throws Exception {
        CompletableFuture<byte[]> expectedMessage = new CompletableFuture<>();

        Future<Session> sessionFuture = client.connect(new WebSocketAdapter() {

            @Override
            public void onWebSocketConnect(Session sess) {
                super.onWebSocketConnect(sess);
                try {
                    byte[] data = "HELLO WORLD".getBytes();
                    sess.getRemote().sendBytes(ByteBuffer.wrap(data));
                }
                catch (IOException e) {
                    e.printStackTrace();
                }
            }

            @Override
            public void onWebSocketBinary(byte[] payload, int offset, int len) {
                byte[] bytes = Arrays.copyOfRange(payload, offset, offset + len);
                expectedMessage.complete(bytes);
            }
        }, URI.create(ws("/socket")));

        Session session = sessionFuture.get();

        assertThat(session, is(notNullValue()));
        assertThat(expectedMessage.get(), equalTo("hello world".getBytes()));

