package com.pragmaticobjects.oo.atom.it;

import com.pragmaticobjects.oo.atom.anno.Atom;
import com.pragmaticobjects.oo.atom.tests.AssertAtomsAreEqual;
import com.pragmaticobjects.oo.atom.tests.AssertAtomsAreNotEqual;
import com.pragmaticobjects.oo.atom.tests.TestCase;
import com.pragmaticobjects.oo.atom.tests.TestsSuite;

import java.util.Objects;

/**
 * Tests suite for explicit atoms equality logic
 *
 * @author Kapralov Sergey
 */
public class ExplicitEqualsHashcodeTest extends TestsSuite {
    /**
     * Ctor.
     */
    public ExplicitEqualsHashcodeTest() {
        super(
            new TestCase(
                "non-atom classes keep original equality semantics",
                new AssertAtomsAreNotEqual(
                    new NotAtom(2, 2),
                    new NotAtom(2, 2)
                )
            ),
            new TestCase(
                "explicit atom classes keep original equality semantics",
                new AssertAtomsAreEqual(
                    new ExplicitAtom(2, 2),
                    new ExplicitAtom(4, 0)
                )
            ),
            new TestCase(
                "implicit atoms follow the specification",
                new AssertAtomsAreEqual(
                    new ImplicitAtom(2, 2),
                    new ImplicitAtom(2, 2)
                )
            )
        );
    }

    //CHECKSTYLE:OFF
    @Atom
    public static class ExplicitAtom {
        private final int a;
        private final int b;

        public ExplicitAtom(int a, int b) {
            this.a = a;
            this.b = b;
        }

        @Override
        public final boolean equals(Object obj) {
            if(obj instanceof ExplicitAtom) {
                ExplicitAtom atom = (ExplicitAtom) obj;
                return Objects.equals(atom.a + atom.b, a + b);
            } else {
                return false;
            }
        }

        @Override
        public final int hashCode() {
            return Objects.hash(a + b);
        }
    }

    public static class ImplicitAtom {
        private final int a;
        private final int b;

        public ImplicitAtom(int a, int b) {
            this.a = a;
            this.b = b;
        }
    }

    @com.pragmaticobjects.oo.atom.anno.NotAtom
    public static class NotAtom {
        private final int a;
        private final int b;

        public NotAtom(int a, int b) {
            this.a = a;
            this.b = b;
        }
    }
}
