package org.yanshen.springframework;

import com.alibaba.fastjson2.JSON;
import com.alibaba.fastjson2.JSONException;
import com.alibaba.fastjson2.JSONReader;
import com.alibaba.fastjson2.JSONWriter;
import com.alibaba.fastjson2.filter.ValueFilter;
import com.alibaba.fastjson2.reader.ObjectReader;
import com.alibaba.fastjson2.reader.ObjectReaderProvider;
import com.alibaba.fastjson2.support.config.FastJsonConfig;
import com.alibaba.fastjson2.support.spring6.http.converter.FastJsonHttpMessageConverter;
import org.yanshen.springframework.formatter.HashidsFormat;
import org.yanshen.springframework.formatter.HashidsFormatterFactory;
import org.yanshen.springframework.utils.AnnotationUtils;
import org.yanshen.springframework.utils.HashidsUtils;
import org.apache.commons.io.IOUtils;
import org.springframework.format.FormatterRegistry;
import org.springframework.http.HttpInputMessage;
import org.springframework.http.converter.HttpMessageConverter;
import org.springframework.http.converter.HttpMessageNotReadableException;
import org.springframework.web.servlet.config.annotation.WebMvcConfigurationSupport;

import java.io.IOException;
import java.io.InputStream;
import java.lang.reflect.Type;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;
import java.util.List;

/**
 * 启动类，自带hashid与id的转换配置，并且使用了FastJson作为Json的序列化工具
 *
 * @author yanshen
 * Created by yanshen on 2019/1/3.
 */
public class WebApplication extends WebMvcConfigurationSupport {

    /**
     * 添加普通输入参数的hashid转换，如@RequestParam, @PathVariable注解的参数
     *
     * @param registry
     */
    @Override
    protected void addFormatters(FormatterRegistry registry) {
        super.addFormatters(registry);
        HashidsFormatterFactory hashidsFormatterFactory = new HashidsFormatterFactory();
        registry.addFormatterForFieldAnnotation(hashidsFormatterFactory);
    }

    /**
     * 添加json输入和输出的hashid转换器，并且依赖使用FastJson，如@RequestBody注解的参数和返回json格式的数据
     *
     * @param converters
     */
    @Override
    protected void configureMessageConverters(List<HttpMessageConverter<?>> converters) {
        FastJsonHttpMessageConverter convert = fastJsonConfigure(getIntegerObjectReader(), getSerializeFilter());
        converters.add(convert);
    }

    /**
     * json输入参数转换器
     *
     * @return
     */
    protected ObjectReader<Integer> getIntegerObjectReader() {
        ObjectReader<Integer> integerObjectReader = new ObjectReader<Integer>() {
            @Override
            public Integer readObject(JSONReader jsonReader, Type fieldType, Object fieldName, long features) {
                // 获取需要转换的类的实例
                Object contentObject = jsonReader.getContext().object;
                // 判断字段属性是否包含HashidsFormat注解
                if (contentObject != null && fieldName != null && AnnotationUtils.fieldHasAnnotation(contentObject.getClass(), fieldName.toString(), HashidsFormat.class)) {
                    if (jsonReader.isNull()) {
                        jsonReader.readNull();
                        return null;
                    }
                    // 字符串Id转换为整型
                    if (jsonReader.isString()) {
                        String value = jsonReader.readString();
                        if (value != null && !value.matches("\\d+")) {
                            Integer intObj = HashidsUtils.decode(value);
                            return intObj;
                        } else if (value != null) {
                            return Integer.parseInt(value);
                        }
                    }
                    if (jsonReader.isInt()) {
                        return jsonReader.readInt32();
                    }
                }
                // Default behavior for Integer
                if (jsonReader.isNull()) {
                    jsonReader.readNull();
                    return null;
                }
                return jsonReader.readInt32();
            }
        };
        return integerObjectReader;
    }

    /**
     * json输出转换器
     *
     * @return
     */
    protected ValueFilter getSerializeFilter() {
        ValueFilter filter = (object, name, value) -> {
            // 判断字段属性是否包含HashidsFormat注解
            if (object != null && AnnotationUtils.fieldHasAnnotation(object.getClass(), name, HashidsFormat.class)) {
                return HashidsUtils.encode((Integer) value);
            }
            return value;
        };
        return filter;
    }

    /**
     * 配置FastJson转换器
     *
     * @param integerObjectReader
     * @param filter
     * @return
     */
    public FastJsonHttpMessageConverter fastJsonConfigure(ObjectReader<Integer> integerObjectReader, ValueFilter filter) {
        FastJsonHttpMessageConverter converter = new FastJsonHttpMessageConverter() {
            @Override
            public Object read(Type type, Class<?> contextClass, HttpInputMessage inputMessage) throws IOException, HttpMessageNotReadableException {
                try {
                    InputStream in = inputMessage.getBody();
                    byte[] bytes = IOUtils.toByteArray(in);
                    Charset charset = getFastJsonConfig().getCharset();
                    if (charset == null) {
                        charset = StandardCharsets.UTF_8;
                    }
                    String json = new String(bytes, charset);
                    // 使用自定义的ObjectReaderProvider
                    JSONReader.Context context = new JSONReader.Context(getFastJsonConfig().getReaderContext());
                    ObjectReaderProvider provider = context.getProvider();
                    provider.register(int.class, integerObjectReader);
                    provider.register(Integer.class, integerObjectReader);
                    return JSON.parseObject(json, type, context);
                } catch (JSONException ex) {
                    throw new HttpMessageNotReadableException("JSON parse error: " + ex.getMessage(), ex, inputMessage);
                } catch (IOException ex) {
                    throw new HttpMessageNotReadableException("I/O error while reading input message", ex, inputMessage);
                }
