package cn.edu.scau.biubiusuisui.util;

import java.lang.reflect.Constructor;
import java.lang.reflect.InvocationTargetException;
import java.lang.reflect.Method;

/**
 * Replacement utility for sun.reflect.misc classes which are not available in Java 17
 */
public class ReflectionUtil {

    /**
     * Replacement for MethodUtil.getMethod
     */
    public static Method getMethod(Class<?> clazz, String methodName, Class<?>... parameterTypes) 
            throws NoSuchMethodException {
        Method method = clazz.getMethod(methodName, parameterTypes);
        method.setAccessible(true);
        return method;
    }

    /**
     * Replacement for MethodUtil.invoke
     */
    public static Object invoke(Method method, Object obj, Object... args)
            throws InvocationTargetException, IllegalAccessException {
        return method.invoke(obj, args);
    }

    /**
     * Replacement for ConstructorUtil.getConstructor
     */
    public static <T> Constructor<T> getConstructor(Class<T> clazz, Class<?>... parameterTypes)
            throws NoSuchMethodException {
        Constructor<T> constructor = clazz.getConstructor(parameterTypes);
        constructor.setAccessible(true);
        return constructor;
    }

    /**
     * Replacement for ReflectUtil.checkPackageAccess
     * In Java 17, security manager is deprecated, so this is a no-op
     */
    public static void checkPackageAccess(Class<?> clazz) {
        // No-op in Java 17 as SecurityManager is deprecated
    }

    /**
     * Replacement for ReflectUtil.newInstance
     */
    public static <T> T newInstance(Class<T> clazz) 
            throws InstantiationException, IllegalAccessException, 
                   InvocationTargetException, NoSuchMethodException {
        Constructor<T> constructor = clazz.getDeclaredConstructor();
        constructor.setAccessible(true);
        return constructor.newInstance();
    }

    /**
     * Replacement for Reflection.getCallerClass
     * Uses StackWalker API available in Java 9+
     */
    public static Class<?> getCallerClass() {
        return StackWalker.getInstance(StackWalker.Option.RETAIN_CLASS_REFERENCE)
                .walk(stream -> stream
                        .skip(2)  // Skip getCallerClass() and the immediate caller
                        .findFirst()
                        .map(StackWalker.StackFrame::getDeclaringClass)
                        .orElse(null));
    }
}

