package com.dream.codec.tlv.spring;

import org.springframework.context.annotation.Import;

import java.lang.annotation.*;

/**
 * Use this annotation to register TLV beans.
 *
 * @author DreamJM
 */
@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.TYPE)
@Documented
@Import(TLVBeanScannerRegistrar.class)
public @interface TLVBeanScan {

    /**
     * Alias for the {@link #basePackages()} attribute. Allows for more concise annotation declarations e.g.:
     * {@code @TLVBeanScan("org.my.pkg")} instead of {@code @TLVBeanScan(basePackages = "org.my.pkg"})}.
     *
     * @return base package names
     */
    String[] value() default {};

    /**
     * Base packages to scan for TLV Bean.
     *
     * @return base package names for scanning TLV beans
     */
    String[] basePackages() default {};

}

