	@Bean
	public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
		ConfigObject protectedResources = Config.getConfig().getObject("security.protected");
		ConfigValue apiSec = Config.getConfig().getValue("security.api_security");
		boolean enableRestFilter = apiSec != null && Boolean.TRUE.equals(apiSec.unwrapped());
		String signinPath = Config.getConfigParam("security.signin", "/signin");
		String signoutPath = Config.getConfigParam("security.signout", "/signout");
		String accessDeniedPath = Config.getConfigParam("security.access_denied", "/403");
		String signoutSuccessPath = Config.getConfigParam("security.signout_success", signinPath);

		if (enableRestFilter) {
			http.authorizeHttpRequests(authz -> authz.requestMatchers(RestRequestMatcher.INSTANCE).permitAll());
		}

		parseProtectedResources(http, protectedResources);

		if (Config.getConfigBoolean("security.csrf_protection", true)) {
			http.csrf(csrf -> csrf
				.requireCsrfProtectionMatcher(CsrfProtectionRequestMatcher.INSTANCE)
				.csrfTokenRepository(csrfTokenRepository));
		} else {
			http.csrf(csrf -> csrf.disable());
		}

		http.sessionManagement(session -> session
			.sessionCreationPolicy(SessionCreationPolicy.NEVER)
			.sessionAuthenticationStrategy(new NullAuthenticatedSessionStrategy()));

		http.exceptionHandling(exceptions -> exceptions
			.authenticationEntryPoint(new SimpleAuthenticationEntryPoint(signinPath))
			.accessDeniedHandler(new SimpleAccessDeniedHandler(accessDeniedPath)));

		http.requestCache(cache -> cache.requestCache(new SimpleRequestCache()));

		http.logout(logout -> logout
			.logoutUrl(signoutPath)
			.logoutSuccessUrl(signoutSuccessPath));

		http.rememberMe(rememberMe -> rememberMe.rememberMeServices(rememberMeServices));

		AuthenticationManager authManager = authenticationManager();
		registerAuthFilters(http, authManager);

		if (jwtFilter != null) {
			jwtFilter.setAuthenticationManager(authManager);
			http.addFilterBefore(jwtFilter, RememberMeAuthenticationFilter.class);
		}
		if (restAuthFilter != null) {
			http.addFilterBefore(restAuthFilter, RememberMeAuthenticationFilter.class);
		}

		return http.build();
	}

