import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.*;

@RestController
@RequestMapping("/admin")
public class AdminController {
    private static final Logger log = LoggerFactory.getLogger(AdminController.class);
    @Autowired
    private AdminManager adminManager;

    @Operation(value = "Get Running State", notes = "Get the current administrative state of the Chaos Engine")
    @GetMapping("/state")
    public AdminState getAdminState () {
        return adminManager.getAdminState();
    }

    @Operation(value = "Set Running State", notes = "Controls whether or not the Chaos Engine will take any action on endpoints. " + "In a STARTED state, all actions can be performed. In a DRAIN state, no new experiments can be created, but existing experiments can still self-heal and finalize. " + "In a PAUSED state, no activity is done.")
    @PostMapping("/state")
    public void setAdminState (@Parameter(value = "The New Admin State.", required = true) @RequestParam("state") AdminState newAdminState) {
        log.info("Setting admin state to {}", newAdminState);
        adminManager.setAdminState(newAdminState);
    }
}

