    public static final String DATE_PATTERN = "yyyy-MM-dd'T'HH:mm:ss.SSSZ";
    private static HashMap<String, Class<? extends Exception>> exceptionMap = new HashMap<>();
    static {
        exceptionMap.put("Validation.invalid", Exception.class);
