    protected <T> T getFeignController(String baseUrl, Feign.Builder feignBuilder, Class<T> target, Logger logger) {
        return feignBuilder.logger(logger).target(target, baseUrl);
    }

    protected Feign.Builder feignBuilder(Client feignClient,
                                         Request.Options options,
                                         java.util.List<RequestInterceptor> requestInterceptors,
                                         Contract feignContract,
                                         Decoder feignDecoder,
                                         Encoder feignEncoder,
                                         Retryer retryer,
                                         Logger.Level logLevel,
                                         feign.codec.ErrorDecoder errorDecoder) {
        Feign.Builder builder = Feign.builder()
                .client(feignClient)
                .options(options)
                .contract(feignContract)
                .decoder(feignDecoder)
                .encoder(feignEncoder)
                .retryer(retryer)
                .logLevel(logLevel);

        if (errorDecoder != null) {
            builder.errorDecoder(errorDecoder);
        }

        if (requestInterceptors != null) {
            builder.requestInterceptors(requestInterceptors);
        }

        return builder;
    }
}

