  private static class XmlAdapterConverter implements Converter<Object, Object> {
    private final XmlAdapter<Object, Object> adapter;
    private final JavaType inputType;
    private final JavaType outputType;

    @SuppressWarnings("unchecked")
    XmlAdapterConverter(XmlAdapter<?, ?> adapter, JavaType inputType, JavaType outputType) {
      this.adapter = (XmlAdapter<Object, Object>) adapter;
      this.inputType = inputType;
      this.outputType = outputType;
    }

    @Override
    public Object convert(Object value) {
      try {
        return adapter.marshal(value);
      } catch (Exception e) {
        throw new IllegalArgumentException("Failed to convert value using XmlAdapter", e);
      }
    }

    @Override
    public JavaType getInputType(TypeFactory typeFactory) {
      return inputType;
    }

    @Override
    public JavaType getOutputType(TypeFactory typeFactory) {
      return outputType;
    }
  }
