package sshd.shell.springboot.util;

import java.io.IOException;
import java.net.ServerSocket;

/**
 * Utility class for finding available TCP ports.
 * Replaces the deprecated org.springframework.util.SocketUtils.
 */
public class SocketUtils {

    /**
     * Find an available TCP port randomly selected from the range [1024, 65535].
     *
     * @return an available TCP port number
     * @throws IllegalStateException if no available port could be found
     */
    public static int findAvailableTcpPort() {
        try (ServerSocket serverSocket = new ServerSocket(0)) {
            return serverSocket.getLocalPort();
        } catch (IOException e) {
            throw new IllegalStateException("Could not find an available TCP port", e);
        }
    }
}

