            tracingDataSource.setJdbcUrl(dataSource.getUrl());
            BeanUtils.copyProperties(dataSource, tracingDataSource);
            return tracingDataSource;
        }

        /**
         * Create a bean of tracing configuration.
         *
         * @param tracingDataSource tracing DataSource
         * @return a bean of tracing configuration
         */
        @Bean
        @ConditionalOnBean(name = "tracingDataSource")
        @ConditionalOnMissingBean(name = "tracingConfigurationWithDefaultDataSource")
        public TracingConfiguration<DataSource> tracingConfiguration(@Qualifier("tracingDataSource") final DataSource tracingDataSource) {
            return new TracingConfiguration<>("RDB", tracingDataSource);
