package com.cloudentity.tools.vertx.conf.ext;

import io.vertx.config.spi.ConfigStore;
import io.vertx.config.spi.ConfigStoreFactory;
import io.vertx.core.Vertx;
import io.vertx.core.json.JsonObject;
import io.vertx.core.spi.VertxServiceProvider;

import java.util.ServiceLoader;

/**
 * Extension of file config store.
 * See {@link ExtConfigStore} for details.
 */
public class FileExtConfigStoreFactory implements ConfigStoreFactory {
  public String name() {
    return "file-ext";
  }

  public ConfigStore create(Vertx vertx, JsonObject configuration) {
    // Find the file config store factory
    ConfigStoreFactory fileFactory = null;
    ServiceLoader<ConfigStoreFactory> loader = ServiceLoader.load(ConfigStoreFactory.class, ConfigStoreFactory.class.getClassLoader());
    for (ConfigStoreFactory factory : loader) {
      if ("file".equals(factory.name())) {
        fileFactory = factory;
        break;
      }
    }

    if (fileFactory == null) {
      throw new IllegalStateException("Cannot find 'file' config store factory");
    }

    return ExtConfigStoreFactory.create(vertx, fileFactory, configuration);
  }
}

