import org.springframework.security.web.session.HttpSessionEventPublisher;
import org.springframework.security.web.util.matcher.AntPathRequestMatcher;

/** managed the web security.
 * generate the access to the pages
 * has a password hash method

 * @author Marco Petzold, Christian König, Danny Steinbrecher
 */
@Configuration
@EnableWebSecurity
public class WebSecurityConfig extends WebSecurityConfigurerAdapter {

    @Autowired
    UserDetailsService accountDetailsService;

    /**
     * @return   HttpSessionEventPublisher
     */
    @Bean
    public HttpSessionEventPublisher httpSessionEventPublisher() {
        return new HttpSessionEventPublisher();
    }

    /**
     * @return   CustomAccessDeniedHandler
     */
    @Bean
    public AccessDeniedHandler accessDeniedHandler(){
        return new CustomAccessDeniedHandler();
    }

    /**
     * @return   BCryptPasswordEncoder
     */
    @Bean
    public PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }

    @Override
    protected void configure(AuthenticationManagerBuilder auth) throws Exception {
        auth.userDetailsService(accountDetailsService);
    }

    /**
     * @param  http HttpSecurity
     * @throws Exception Exception
     * configure the access to the pages
     * configure the login
     * configure the logout
     * configure the accessDeniedHandler
     * configure the SessionCreationPolicy
     */
    @Override
    protected void configure(HttpSecurity http) throws Exception {

        http
                .authorizeRequests()
                    .antMatchers().denyAll()
                    .antMatchers("/", "/login", "/logout",  "/register",  "/save", "h2-console/**", "/tools").permitAll()
                    .antMatchers("/userManagement", "/customer", "/employee").hasRole("ADMIN")
                    .antMatchers("/toolManagement", "/addTool", "/editTool", "/delete").hasAnyRole("EMPLOYEE", "ADMIN")
                    .antMatchers("/accountView", "/billForm", "/billView", "/rentSuccessful", "/returnSuccessful",
                                            "/rentedToolView",  "/chooseStation",  "/returnStation", "/editCustomerAccount").hasAnyRole("CUSTOMER")
                    .and()

                .formLogin()
                    .loginPage("/login")
                    .failureUrl("/login?error")
                    .usernameParameter("username").passwordParameter("password")
                    .defaultSuccessUrl("/loginSuccessfull", true)
                    .permitAll()
                    .and()

                .logout()
                    .logoutRequestMatcher(new AntPathRequestMatcher("/logout"))
                    .logoutSuccessUrl("/login?logout")
                    .and()

                .exceptionHandling()
                    .accessDeniedHandler(accessDeniedHandler()).and()

                .sessionManagement()
                    .sessionCreationPolicy(SessionCreationPolicy.IF_REQUIRED);

        http.csrf().disable();
        http.headers().frameOptions().disable();
    }
}

