// ------------------------------------------------------------------------
// Private utils
// ------------------------------------------------------------------------

private SecretKey getSigningKey() {
    byte[] keyBytes = secret.getBytes(StandardCharsets.UTF_8);
    return Keys.hmacShaKeyFor(keyBytes);
}

private String generateToken(Map<String, Object> claims) {

    final Date issuedAtDate = (Date) claims.get(CLAIM_KEY_ISSUED_AT);
    final Date expirationDate = new Date(issuedAtDate.getTime() + (expirationInSecs * 1000));

    return Jwts.builder()
            .claims(claims)
            .expiration(expirationDate)
            .issuedAt(issuedAtDate) // TODO Possible bug in jjwt - if this not set to override claims value, date is WAY in the future...
            .signWith(getSigningKey())
            .compact();
}

private Claims getClaimsFromToken(String token) {
    return Jwts.parser()
            .clockSkewSeconds(allowedClockSkewInSecs)
            .verifyWith(getSigningKey())
            .requireIssuer(issuer)
            .requireAudience(audience)
            .build()
            .parseSignedClaims(token)
