package com.gitile.fast.core.mybatis;

import com.baomidou.mybatisplus.generator.FastAutoGenerator;
import com.baomidou.mybatisplus.generator.config.DataSourceConfig;
import com.baomidou.mybatisplus.generator.config.OutputFile;
import com.baomidou.mybatisplus.generator.config.rules.NamingStrategy;
import com.baomidou.mybatisplus.generator.engine.FreemarkerTemplateEngine;
import com.gitile.fast.core.utils.FilenameUtils;

import java.util.Collections;

/**
 * 代码生成工具类
 */
public class FastCodeGenerator {

	/**
	 * 构造方法
	 */
	private FastCodeGenerator() {
		throw new IllegalStateException("Utility class");
	}

	/**
	 * 使用mybatisplus的FastAutoGenerator进行model、dao、mapper的生成
	 */
	public static void autoPersistenceMysql(String basePakcage, String author, DataSourceConfig dsc) {
		FastAutoGenerator.create(dsc)
			.globalConfig(builder -> {
				builder.author(author) // 设置作者
					.outputDir(getMavenJavaPath()) // 输出目录
					.disableOpenDir(); // 禁止打开输出目录
			})
			.packageConfig(builder -> {
				builder.parent(basePakcage) // 设置父包名
					.pathInfo(Collections.singletonMap(OutputFile.xml, getMavenResourcesPath())); // 设置mapperXml生成路径
			})
			.strategyConfig(builder -> {
				builder.addInclude(".*") // 设置需要生成的表名，这里使用正则匹配所有表
					.entityBuilder()
					.enableActiveRecord() // 开启AR模式
					.enableLombok() // 开启lombok
					.naming(NamingStrategy.underline_to_camel) // 数据库表映射到实体的命名策略
					.columnNaming(NamingStrategy.underline_to_camel) // 数据库表字段映射到实体的命名策略
					.mapperBuilder()
					.enableBaseResultMap() // 生成通用的resultMap
					.enableBaseColumnList(); // 生成通用的sql列
			})
			.templateEngine(new FreemarkerTemplateEngine()) // 使用Freemarker引擎模板，默认的是Velocity引擎模板
			.execute();
	}

	public static String getMavenJavaPath() {
		return System.getProperty("user.dir") + FilenameUtils.separatorsToSystem("\\src\\main\\java\\");
	}

	public static String getMavenResourcesPath() {
		return System.getProperty("user.dir") + FilenameUtils.separatorsToSystem("\\src\\main\\resources\\mapper\\");
	}

}

