import org.eclipse.jetty.websocket.api.Session;
import org.eclipse.jetty.websocket.api.annotations.*;
import org.eclipse.jetty.websocket.client.ClientUpgradeRequest;
import org.eclipse.jetty.websocket.client.WebSocketClient;
import org.junit.After;
import org.junit.Before;
import org.junit.Test;

import java.io.IOException;
import java.net.URI;
import java.nio.ByteBuffer;
import java.util.Arrays;
import java.util.concurrent.CompletableFuture;

import static org.hamcrest.CoreMatchers.*;
import static org.junit.Assert.*;
import static org.nosceon.titanite.Response.webSocket;

/**
 * @author Johan Siebens
 */
public class WebSocketsTest extends AbstractE2ETest {

    private WebSocketClient client;

    @Override
    protected Shutdownable configureAndStartHttpServer(HttpServer server) throws Exception {
        return server
            .register(Method.GET, "/socket", req -> webSocket(channel -> {
                channel.onTextMessage(s -> channel.write(s.toUpperCase()));
                channel.onBinaryMessage(b -> {
                    String s = new String(b);
                    channel.write(s.toLowerCase().getBytes());
                });
            }))
            .start();
    }

    @Before
    public void setUp() throws Exception {
        this.client = new WebSocketClient();
        client.start();
    }

    @After
    public void tearDown() throws Exception {
        client.stop();
    }

    @WebSocket
    public static class TextSocket {
        private int count = 0;
        private CompletableFuture<String> expectedMessage;

        public TextSocket(CompletableFuture<String> expectedMessage) {
            this.expectedMessage = expectedMessage;
        }

        @OnWebSocketConnect
        public void onConnect(Session session) {
            try {
                session.getRemote().sendString("hello world 1");
                session.getRemote().sendString("hello world 2");
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        @OnWebSocketMessage
        public void onMessage(String message) {
            count++;
            if (count == 2) {
                expectedMessage.complete(message);
            }
        }
    }

    @WebSocket
    public static class BinarySocket {
        private CompletableFuture<byte[]> expectedMessage;

        public BinarySocket(CompletableFuture<byte[]> expectedMessage) {
            this.expectedMessage = expectedMessage;
        }

        @OnWebSocketConnect
        public void onConnect(Session session) {
            try {
                byte[] data = "HELLO WORLD".getBytes();
                session.getRemote().sendBytes(ByteBuffer.wrap(data));
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        @OnWebSocketMessage
        public void onMessage(byte[] data, int offset, int length) {
            byte[] bytes = Arrays.copyOfRange(data, offset, offset + length);
            expectedMessage.complete(bytes);
        }
    }

    @Test(timeout = 2000)
    public void testText() throws Exception {
        CompletableFuture<String> expectedMessage = new CompletableFuture<>();

        TextSocket socket = new TextSocket(expectedMessage);
        ClientUpgradeRequest request = new ClientUpgradeRequest();
        Session session = client.connect(socket, URI.create(ws("/socket")), request).get();

        assertThat(session, is(notNullValue()));
        assertThat(expectedMessage.get(), equalTo("HELLO WORLD 2"));

        session.close();
    }

    @Test(timeout = 2000)
    public void testBinary() throws Exception {
        CompletableFuture<byte[]> expectedMessage = new CompletableFuture<>();

        BinarySocket socket = new BinarySocket(expectedMessage);
        ClientUpgradeRequest request = new ClientUpgradeRequest();
        Session session = client.connect(socket, URI.create(ws("/socket")), request).get();

        assertThat(session, is(notNullValue()));
        assertThat(expectedMessage.get(), equalTo("hello world".getBytes()));

        session.close();
    }

}

