import static org.nosceon.titanite.scope.Session.enableSessions;
import static org.nosceon.titanite.scope.Session.session;
import static org.nosceon.titanite.scope.SessionFilter.DEFAULT_SESSION_COOKIE_NAME;

/**
 * @author Johan Siebens
 */
public class SessionE2ETest extends AbstractE2ETest {

    @Override
    protected Shutdownable configureAndStartHttpServer(HttpServer server) {
        return
            server
                .setFilter(enableSessions("my-favorite-secret"))
                .register(GET, "/a", (r) -> {
                    session(r).set("name", "titanite");
                    session(r).set("lorem", "ipsum");
                    return ok().toFuture();
                })
                .register(GET, "/b",
                    req -> {
                        int count = session(req).getInt("count", 0);
                        session(req).set("count", count + 1);
                        return ok().text(String.valueOf(count)).toFuture();
                    }
                )
                .register(GET, "/c",
                    req -> {
                        String name = session(req).getString("lorem");
                        session(req).clear();
                        return ok().text(name).toFuture();
                    }
                )
                .start();
    }

    @Test
    public void testA() {
        given().expect().statusCode(200).cookie(DEFAULT_SESSION_COOKIE_NAME, "\"ddf5cc7e5f4a7d024bbf6cd3344fd6c306c5a20e|lorem=ipsum&name=titanite\"").when().get(uri("/a"));
    }

