        given()
            .cookie(DEFAULT_SESSION_COOKIE_NAME, "\"invalid_signature|count=5\"")
            .expect().statusCode(200).cookie(DEFAULT_SESSION_COOKIE_NAME, "\"adfb9b2f6426f5b01d0bf50eb2e9c9fa9239d212|count=1\"").when().get(uri("/b"));
    }

}
