  }

  public String getFirstValue() {
    return this.body.getQuery().entrySet().iterator().next().getValue();
  }

  @Override
  public boolean equals(Object obj) {

    if (obj == null) {
      return false;
    }

    if (this.getClass() != obj.getClass()) {
      return false;
    }

    Request target = (Request) obj;
    String targetTrName = target.getBody().getTrName();

    Map.Entry<String, String> targetEntry = target.getBody().getQuery().entrySet().iterator()
        .next();

    if (target.getBody().getQuery().size() != 1 || this.body.getQuery().size() != 1) {
      return false;
    }

    if (this.body.getTrName().equals(targetTrName) && getFirstValue().equals(
        targetEntry.getValue())) {
      return true;
    }

    return false;
  }

  @Override
  public int hashCode() {
    return Objects.hash(this.body.getTrName(), getFirstValue());
  }
}
