    // read from bcif
    InputStream inputStream = TestHelper.getInputStream("snapshot/" + testCase + ".bcif");
    CifFile binary = CifIO.readFromInputStream(inputStream);

    // convert to bcif
    byte[] output = CifIO.writeBinary(binary);
    byte[] outputGzip = CifIO.writeBinary(binary, CifOptions.builder().gzip(true).build());

    assertEquals("failed for " + testCase, new String(original), new String(output));

    // For GZIP comparison, decompress both files and compare the content
    // GZIP headers can differ between Java versions (timestamp, OS flags, etc.)
    byte[] decompressedOriginalGzip = decompressGzip(originalGzip);
    byte[] decompressedOutputGzip = decompressGzip(outputGzip);
    assertArrayEquals("failed (gzip) for " + testCase + " - decompressed content differs", 
            decompressedOriginalGzip, decompressedOutputGzip);

    // cannot match to David's bcif data as column types differ slightly
    assertArrayEquals("binary write output does not match snapshot of output - did the implementation change?" +
            " if so, update snapshot files in snapshot/", original, output);
}

private byte[] decompressGzip(byte[] gzipData) throws IOException {
    try (java.io.ByteArrayInputStream bais = new java.io.ByteArrayInputStream(gzipData);
         java.util.zip.GZIPInputStream gis = new java.util.zip.GZIPInputStream(bais);
         java.io.ByteArrayOutputStream baos = new java.io.ByteArrayOutputStream()) {
        byte[] buffer = new byte[1024];
        int len;
        while ((len = gis.read(buffer)) > 0) {
            baos.write(buffer, 0, len);
        }
        return baos.toByteArray();
    }
}

