@ExtendWith(MockitoExtension.class)
public class TravelAgentTest {

	@Mock
	private RestTemplate restTemplate;

	@Mock
	private CircuitBreakerFactory circuitBreakerFactory;

	private TravelAgent travelAgent;

	@BeforeEach
	public void setUp() throws Exception {
		// Create a simple circuit breaker that executes the supplier
		given(circuitBreakerFactory.create(anyString())).willReturn(
			new org.springframework.cloud.client.circuitbreaker.CircuitBreaker() {
				@Override
				public <T> T run(java.util.function.Supplier<T> toRun, java.util.function.Function<Throwable, T> fallback) {
					try {
						return toRun.get();
					} catch (Throwable t) {
						return fallback.apply(t);
					}
				}

				@Override
				public <T> T run(java.util.function.Supplier<T> toRun) {
					return toRun.get();
				}
			}
		);
		this.travelAgent = new TravelAgent(restTemplate, circuitBreakerFactory);
	}

	@Test
	public void getGuide() {
		given(restTemplate.getForObject("https://company/available", String.class))
				.willReturn("James");
		String guide = this.travelAgent.getGuide();
		assertThat(guide).isEqualTo("James");
	}

	@Test
	public void getBackupGuide() {
		String guide = this.travelAgent.getBackupGuide();
		assertThat(guide).isEqualTo("None available! Your backup guide is: Cookie");
	}

}

