@TestFactory
Stream<DynamicTest> dynamicTests() {
    registerTests();
    ThrowingConsumer<IntegrationTestConfig> testExecutor = running -> {
        final Liquibase liquibase = LiquibaseIntegrationTestResolver.buildLiquibase(running.getChangeLogFile(), running.getConfigFile());
        final Writer nullWriter = CharStreams.nullWriter();
        final Contexts contexts = new Contexts();
        if (running.getMessage() != null) {
            // In Liquibase 4.x, ChangeLogParseException is wrapped in CommandExecutionException
            try {
                liquibase.update(contexts, nullWriter);
                throw new AssertionError("Expected exception but none was thrown");
            } catch (CommandExecutionException e) {
                // Check if the cause is a ChangeLogParseException
                assertThat(e.getCause()).isInstanceOf(ChangeLogParseException.class);
                assertThat(e.getMessage()).contains(running.getMessage());
            } catch (ChangeLogParseException e) {
                // For backwards compatibility with older Liquibase versions
                assertThat(e.getMessage()).contains(running.getMessage());
            }
        } else {
            liquibase.update(contexts, nullWriter);
        }
    };
    return DynamicTest.stream(tests.iterator(), IntegrationTestConfig::getDisplayName, testExecutor);
}

