private static class ConfigAwareFileSystemResourceAccessor extends FileSystemResourceAccessor {

    private final String configPath;
    private final ClassLoaderResourceAccessor classLoaderAccessor;

    private ConfigAwareFileSystemResourceAccessor(java.nio.file.Path basePath, String configPath) {
        super(basePath.toFile());
        this.configPath = configPath;
        this.classLoaderAccessor = new ClassLoaderResourceAccessor();
    }

    @Override
    public List<Resource> search(String path, boolean recursive) throws IOException {
        if (ConfigLoader.LQLINT_CONFIG.equals(path)) {
            List<Resource> resources = new ArrayList<>();
            InputStream stream = getClass().getResourceAsStream(configPath);
            if (stream != null) {
                resources.add(new InputStreamResource(configPath, stream));
            }
            return resources;
        }
        return super.search(path, recursive);
    }

}

