private Config loadConfig(ResourceAccessor resourceAccessor, String path) throws IOException {
    List<Resource> resources = resourceAccessor.search(path, true);

    // If not found, try with various path variations (for backwards compatibility)
    if (resources == null || resources.isEmpty()) {
        // Try stripping "src/test/resources/" or "src/main/resources/" prefix if present
        String simplifiedPath = path;
        if (simplifiedPath.startsWith("src/test/resources/")) {
            simplifiedPath = simplifiedPath.substring("src/test/resources/".length());
            resources = resourceAccessor.search(simplifiedPath, true);
        } else if (simplifiedPath.startsWith("src/main/resources/")) {
            simplifiedPath = simplifiedPath.substring("src/main/resources/".length());
            resources = resourceAccessor.search(simplifiedPath, true);
        }
    }

    if (resources != null && !resources.isEmpty()) {
        try (InputStream inputStream = resources.get(0).openInputStream()) {
            return loadImports(resourceAccessor, Config.fromInputStream(inputStream));
        }
    }
    return null;
}

