private Config loadConfig(ResourceAccessor resourceAccessor, String path) throws IOException {
    List<Resource> resources = null;
    String actualPath = path;

    // Try stripping "src/test/resources/" or "src/main/resources/" prefix if present
    if (actualPath.startsWith("src/test/resources/")) {
        actualPath = actualPath.substring("src/test/resources/".length());
    } else if (actualPath.startsWith("src/main/resources/")) {
        actualPath = actualPath.substring("src/main/resources/".length());
    }

    // In Liquibase 4.x, we need to get the parent directory and then search for the file
    int lastSlash = actualPath.lastIndexOf('/');
    if (lastSlash > 0) {
        String directory = actualPath.substring(0, lastSlash);
        String filename = actualPath.substring(lastSlash + 1);

        try {
            List<Resource> dirResources = resourceAccessor.search(directory, false);
            if (dirResources != null) {
                // Filter for our specific file
                resources = dirResources.stream()
                    .filter(r -> r.getPath().endsWith(filename))
                    .collect(java.util.stream.Collectors.toList());
            }
        } catch (IOException e) {
            // Directory doesn't exist or other error
        }
    }

    // Fallback: try the path directly
    if (resources == null || resources.isEmpty()) {
        try {
            resources = resourceAccessor.search(actualPath, false);
        } catch (IOException e) {
            // Path doesn't exist
        }
    }

    if (resources != null && !resources.isEmpty()) {
        try (InputStream inputStream = resources.get(0).openInputStream()) {
            return loadImports(resourceAccessor, Config.fromInputStream(inputStream));
        }
    }
    return null;
}

