package graphql.sql.core;

import graphql.ExecutionResult;
import graphql.ExecutionResultImpl;
import graphql.execution.ExecutionContext;
import graphql.execution.ExecutionStrategy;
import graphql.execution.ExecutionParameters;
import graphql.execution.ValuesResolver;
import graphql.language.Field;
import graphql.schema.DataFetchingEnvironment;
import graphql.schema.DataFetchingEnvironmentImpl;
import graphql.schema.GraphQLFieldDefinition;

import java.util.Collections;
import java.util.List;
import java.util.Map;
import java.util.concurrent.CompletableFuture;

public class FieldExecutionStrategy extends ExecutionStrategy {

    private final ValuesResolver valuesResolver = new ValuesResolver();

    @Override
    public CompletableFuture<ExecutionResult> execute(ExecutionContext executionContext, ExecutionParameters parameters) {
        // Simple synchronous implementation
        return CompletableFuture.completedFuture(new ExecutionResultImpl(null, Collections.emptyList()));
    }

    public Object executeField(GraphQLFieldDefinition fieldDef, Field field, ExecutionContext executionContext) {
        List<Field> fields = Collections.singletonList(field);

        Map<String, Object> argumentValues = valuesResolver.getArgumentValues(fieldDef.getArguments(), field.getArguments(), executionContext.getVariables());
        DataFetchingEnvironment environment = new DataFetchingEnvironmentImpl(
                executionContext.getRoot(),
                argumentValues,
                executionContext.getRoot(),
                fields,
                fieldDef.getType(),
                executionContext.getGraphQLSchema().getQueryType(),
                null,
                executionContext.getFragmentsByName(),
                executionContext.getRoot(),
                fieldDef,
                executionContext.getGraphQLSchema()
        );

        Object resolvedValue = fieldDef.getDataFetcher().get(environment);

        return resolvedValue;
    }
}

